import React, { useState, useEffect } from 'react';
import ProfileCarousel from '../components/Design/ProfileCarousel'
import { Container } from 'react-bootstrap'
import Web from '../assets/images/Backgrounds/pedroLastaArches.jpg'
import Mobile from '../assets/images/Backgrounds/IanChenArches.jpg'
const homeBackground = window.innerWidth >= 650 ? Web : Mobile;

const Home = () => {

    const [windowWidth, setWindowWidth ] = useState(window.innerWidth);

    useEffect(() => {        
        window.addEventListener('resize', handleWindowResize);
      return () => {            
          window.removeEventListener('resize', handleWindowResize);      
        }  
      }, []); //empty array makes side effect only run on mount and unmount


    return(
        <div style={styles.wallpaper} >
            <Container>

                <div style={styles.brand}>
                    <h1>Full Stack Web Developer with a background in business aiming to bridge user experience with client needs. </h1>
                </div>

                <div className="card mb-5">
                    <div className="card-body">
                    My name is Steven Mann, an aspiring web developer in Houston, TX, proficient in several technolgoies including: Javascript, JQuery, React, MySQl and Node.js. 
                    Experience building, designing and styling personal websites using various languages and tools like HTML, CSS, JQuery to design fully functional web platforms. 
                    Passionate about merging functionality with creativity in a high pressure environment. In previous work experience, I was able to utilize and merge client inventory into a proprietary application, giving the client the ability to view, order and maintain inventory. 
                    Versed in collaborative work environments to ensure optimal results for user-centered designs. 
                    <br />
                    <br />
                    When I am not working on my career, I enjoy writing, recording and performing music across the state of
                    Texas.
                    I perform in several local bands and enjoy playing genres from country and americana to blues and rock.
                    I
                    have opened
                    for acts such as Kyle Hollingsworth, Dumpstafunk, Naughty Professor, George Porter Jr., Organ Freeman
                    and
                    The Texas Gentlemen.
                    I also enjoy outdoor activities such as biking, fishing, and camping. On my most recent camping trip, I
                    traveled to Lost Maples
                    just outside of San Antonio, spending four days and nights
                    in the remote Texas hillcountry.
                    </div>
                </div>
            </Container>
        </div>

    )
}

const styles ={
    wallpaper: {
        backgroundImage: `url(${homeBackground})`,
        height: '100vh',
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'top center'
    },
    brand: {
        fontStyle: 'italic',
    },

}

export default Home;
