import React from 'react'
import { Row, Col, Image, Container } from 'react-bootstrap'
import {useSpring, animated} from 'react-spring'
import { Link } from 'react-scroll'


const Banner = () => {

    const fadeInTop = useSpring({
        config: {duration: 1000},
        from: {opacity: 0, marginTop: -80},
        to: {opacity: 1, marginTop: 0},
    })

    const MouseDownHandler = (e) => {
        if (e.type === 'mousedown'){

        }
    }

    return (
        <div className=".img-fluid wallpaper" style={styles.bannerWrapper}>
            <Container>
                <Row>
                    <Col xs={12} sm={12} md={10} lg={10} xl={6}>
                        <animated.div style={fadeInTop}>
                            <div style={styles.brand}>
                                <h1>Full Stack Web Developer</h1>
                                <h2>Bridging UX & UI to clients needs. </h2>
                            </div>
                        </animated.div> 
                    </Col>
                </Row>
                <Row>
                    <Link to='about' smooth={true} duration={2000}>
                        <div style={styles.buttonDiv} style={{backgroundColor: 'red'}}onMouseDown={MouseDownHandler}>
                            <h3 style={styles.buttonText}>About Me</h3>   
                        </div>
                    </Link>
                </Row>
        </Container>
    </div>
    )
}


const styles = {
    bannerWrapper: {
        backgroundColor: '#353836',
        height: '100vh',    
    },
    brand: {
        fontStyle: 'italic',
        color: 'white'
    },
    buttonDiv: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        color: 'white',
        backgroundColor: 'white',
        border: '0.05em solid black',
        borderRadius: '4em',
        width: '10em',
        height: '4em',
        cursor: 'pointer'
    },
    buttonText: {
        color: 'black',
        textAlign: 'center',    
    }
}
export default Banner;
