import React, { useState } from 'react';
import { Carousel, Row, Col } from 'react-bootstrap'





const ProfileCarousel = () => {

    const [index, setIndex] = useState(0);
    const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
    };

    return (

    <Row>
        <Col>
            <Carousel activeIndex={index} onSelect={handleSelect} >
            <Carousel.Item style={styles.imgContainer}>
                <img
                className="d-block w-100"
                src={require("../../assets/images/Personal/PresentCompany.JPG")}
                alt="2018 Telluride Bluegrass Festival"
                />
                <Carousel.Caption>
                <h3>First slide label</h3>
                <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                </Carousel.Caption>
            </Carousel.Item>

            <Carousel.Item style={styles.imgContainer}>
                <img
                className="d-block w-100"
                src={require("../../assets/images/Personal/Fiesta.JPG")}
                alt="A Colorado morning"
                />
                <Carousel.Caption>
                <h3>Second slide label</h3>
                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                </Carousel.Caption>
            </Carousel.Item>

            <Carousel.Item style={styles.imgContainer}>
                <img
                className="d-block w-100"
                src={require("../../assets/images/MexicoCity.JPG")}
                alt="Mexico City skyline"
                />

                <Carousel.Caption>
                <h3>Third slide label</h3>
                <p>
                    Praesent commodo cursus magna, vel scelerisque nisl consectetur.
                </p>
                </Carousel.Caption>
            </Carousel.Item>
            </Carousel>
        </Col>
     </Row>

      );
}

const styles = {

    imgContainer : {
        height: '100vh',
    }
}


export default ProfileCarousel;
