import React, { useState } from 'react';
import emailjs from 'emailjs-com';
import { Form } from 'react-bootstrap'

const ContactForm = () => {

    const [emailText, setEmailText] = useState("")

  function sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm('stevensgmail', 'contact_form', e.target, 'user_iE2OnCTdGFWesLXzFqm3O')
      .then((result) => {
          console.log("iaunsdoiunaosdnoasdn ",result)
          console.log(result.text);
      }, 
      (error) => {
          console.log(error.text);
      });
  }

  const emailChange =(e)=>{
      setEmailText(e.target.value) 
  }



  return (

    <Form onSubmit={sendEmail}>
    <Form.Group controlId="contactForm.Name" value={emailText} onChange={emailChange} >
      <Form.Label>Name</Form.Label>
      <Form.Control type="name" placeholder="John Smith" />
    </Form.Group>
    <Form.Group controlId="contactForm.Email" value={emailText} onChange={emailChange} >
      <Form.Label>Email address</Form.Label>
      <Form.Control type="email" placeholder="name@example.com" />
    </Form.Group>
    <Form.Group controlId="exampleForm.ControlSelect1">
      <Form.Label>Example select</Form.Label>
      <Form.Control as="select">
        <option>1</option>
        <option>2</option>
        <option>3</option>
        <option>4</option>
        <option>5</option>
      </Form.Control>
    </Form.Group>
    <Form.Group controlId="exampleForm.ControlSelect2">
      <Form.Label>Example multiple select</Form.Label>
      <Form.Control as="select" multiple>
        <option>1</option>
        <option>2</option>
        <option>3</option>
        <option>4</option>
        <option>5</option>
      </Form.Control>
    </Form.Group>
    <Form.Group controlId="exampleForm.ControlTextarea1">
      <Form.Label>Example textarea</Form.Label>
      <Form.Control as="textarea" rows="3" />
    </Form.Group>
    <input type="submit" value="Send" />
  </Form>



  );
}

