import React from 'react';
import { Image, Row, Col, Card } from 'react-bootstrap'



const ProjectsCard = props =>{

    return(

        <Row style={styles.projectDiv} key={props.id}>
            <Col xs={12} sm={12} md={6} lg={6} xl={6}>
                <Image src={require('../../assets/images/Projects' + props.image)} 
                    style={styles.projImg} fluid/>
            </Col>
            <Col xs={12} sm={12} md={6} lg={6} xl={6}>
              <Card.Body style={styles.cardBody}>
                <Card.Title className="text-center">
                    {props.title}
                </Card.Title>
                <Card.Text className="text-center">
                    {props.description}
                </Card.Text>
                <div style={styles.iconsDiv}>
                    {props.icons ? (props.icons.map((item, index )=>
                        <Image src={require('../../assets/images/Icons' + item)} style={styles.logo} key={index}/>
                        )):(null)}
                </div>
              </Card.Body>
            </Col>
        </Row>

    )

}

const styles={
    projectDiv:{
        marginTop: '2em',
        marginBottom: '2em'
    },

   cardBody: {
       display: 'flex',
       flexDirection: 'column', 
       alignItems: 'center',
   },
   iconsDiv: {
    marginTop: '2em'
   },
   logo: {
    height: '3em',
    width: '3em',
    color: 'white',
    margin: '0.4em'   
},
}

export default ProjectsCard;
