import React from 'react';
import Loader from 'react-loader-spinner';
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css"

const Loading = () => {

return(

    <div style={styles.textAlign}>
        <Loader
            type="Bars"
            color="#09FBD3"
            height={400}
            width={400}
            timeout={3000} //3 secs
        />
    </div>

    )   

}

const styles = {
    container : {
        textAlign: 'center'
    }
}

export default Loading;
