import React, { useState } from 'react';
import CardPic from '../components/Design/CardPic'
import { Container, Row, Col } from 'react-bootstrap'
import Squares from '../assets/images/Backgrounds/GabrielBassinoSquares.jpg'
import '../assets/css/home.css'
import portfolioData from '../assets/portfolioData.json'
import { useTrail, animated } from 'react-spring'

const Portfolio = () => {
    const items = portfolioData
    const config = { mass: 5, tension: 2000, friction: 200 }
    const [showProjects, setShowProjects] = useState(true)
    const trail = useTrail(items.length, {
      config,
      opacity: showProjects ? 1 : 0,
      x: showProjects ? 0 : 20,
      height: showProjects ? 80 : 0,
      from: { opacity: 0, x: 20, height: 0 },
    })

    const projectsHandler = () =>{
        setShowProjects(!showProjects)
    }

    return(
        <div className="wallpaper" style={{backgroundImage: `url(${Squares})`}}>
            <Container style={styles.screenContainer}>
                <Row>
                    <Col xl={12} large={12} md={12} sm={12} xs={12}>
                        <div className="text-center bg-success" onClick={projectsHandler}>
                            <h1>CLICK ME FOR PROJECTS!!!!!!!!</h1>
                        </div>
                    </Col>
                </Row>
                <Row>


                </Row>
            </Container>  
        </div>
    )
}

