import React, { useState } from 'react';
import { Carousel, Row, Col } from 'react-bootstrap'





const ProfileCarousel = () => {

    const [index, setIndex] = useState(0);
    const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
    };

    return (

    <Row>
        <Col>
        <Carousel activeIndex={index} onSelect={handleSelect} style={styles.CarouselContainer}>
          <Carousel.Item>
            <img
              className="d-block w-100"
              src={require("../../assets/images/GithubProfilePic.JPG")}
              alt="My trip to the 2018 Telluride Bluegrass Festival"
            />
            <Carousel.Caption>
              <h3>First slide label</h3>
              <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <img
              className="d-block w-100"
              src={require("../../assets/images/LinkdInProfilePic.jpg")}
              alt="A picture of me on a Colorado morning"
            />



            <Carousel.Caption>
              <h3>Third slide label</h3>
              <p>
                Praesent commodo cursus magna, vel scelerisque nisl consectetur.
              </p>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>
        </Col>
        </Row>

      );
}

const styles={
    CarouselContainer: {
        maxWidth: '80%',
        width: '500px',

    }
}

export default ProfileCarousel;
