import React, {  useState, useEffect, useRef, Suspense, lazy } from 'react';
import { Container, Row, Col, Image } from 'react-bootstrap'
import {useSpring, useChain, animated} from 'react-spring'
import * as easings from 'd3-ease'
import Web from '../assets/images/Backgrounds/ErsinMandalievRGB.jpg'
import Mobile from '../assets/images/Backgrounds/EfeKurnazHallway.jpg'
import ProfileCarousel from '../components/Design/ProfileCarousel'
import AboutCard from '../components/Design/AboutCard'
import '../assets/css/home.css'




const Home = () => {

    const [windowWidth, setWindowWidth ] = useState(window.innerWidth);
    const backdrop = windowWidth >= 650 ? Web : Mobile;

    const handleWindowResize = () => {
        setWindowWidth(window.innerWidth);
    };

    useEffect(() => {
        window.addEventListener('resize', handleWindowResize);

        return () => {
            window.removeEventListener('resize', handleWindowResize);
        }
    });


    const topRef = useRef();
    const leftRef = useRef();

    const fadeTop = useSpring({
        config: {duration: 2000},
        from: {opacity: 0, marginTop: -1000},
        to: {opacity: 1, marginTop: 0},
        delay: 1000,
        ref: topRef
    })

    const fadeLeft = useSpring({
        config: {duration: 2000, easing: easings.easeSinInOut},
        from: {opacity: 0},
        to: {opacity: 1},
        delay: 1000,
        ref: leftRef
    })

    useChain([topRef, leftRef])

    return(

            <div className="wallpaper" style={{backgroundImage: `url(${backdrop})`}}>
                <Container style={styles.screenContainer}>
                    <Row>
                        <animated.div style={fadeTop}>
                            <div style={styles.brand}>
                                <h1>Full Stack Web Developer with a background in business aiming to bridge user experience with client needs. </h1>
                            </div>
                        </animated.div> 
                    </Row>
                    <Row>
                    <animated.div style={fadeLeft}>
                        <Col xs={12} sm={12} md={6} lg={6} xl={6}>
                            <ProfileCarousel />
                        </Col>
                    </animated.div>

                        <Col>
                            <AboutCard />
                        </Col>
                    </Row>

                    <Row className="justify-content-center m-5">

                        <Image src={require('../assets/images/Icons/NodeLogo.svg')} style={styles.logo} />
                        <Image src={require('../assets/images/Icons/react.png')} style={styles.logo} />
                        <Image src={require('../assets/images/Icons/javascript.png')} style={styles.logo} />

                    </Row>

                </Container>
            </div>



    )
}

const styles ={

    brand: {
        fontStyle: 'italic',
        color: 'white'
    },
    screenContainer: {
        paddingTop: '4em',
        paddingBottom: '10em',
    },
    logo: {
        height: '4em',
        size: '4em',
        margin: '1em'
    }


}



export default Home;
