import React, { useState } from 'react';
import CardPic from '../components/Design/CardPic'
import { Container, Row, Col } from 'react-bootstrap'
import Squares from '../assets/images/Backgrounds/GabrielBassinoSquares.jpg'
import '../assets/css/home.css'
import portfolioData from '../assets/portfolioData.json'
import { useTrail, animated } from 'react-spring'

const Portfolio = () => {
    const items = portfolioData
    const config = { mass: 5, tension: 2000, friction: 200 }



    const [showProjects, setShowProjects] = useState(true)
    const trail = useTrail(items.length, {
      config,
      opacity: toggle ? 1 : 0,
      x: toggle ? 0 : 20,
      height: toggle ? 80 : 0,
      from: { opacity: 0, x: 20, height: 0 },
    })

    const projectsHandler = () =>{
        setShowProjects(!showProjects)
    }

    return(
        <div className="wallpaper" style={{backgroundImage: `url(${Squares})`}}>
            <Container style={styles.screenContainer}>
                <Row>
                    <Col xl={12} large={12} md={12} sm={12} xs={12}>
                        <div className="text-center bg-success" onClick={projectsHandler}>
                            <h1>CLICK ME FOR PROJECTS!!!!!!!!</h1>
                        </div>
                    </Col>
                </Row>
                <Row>
                    {portfolioData.map(item=>(
                    <Col  xl={4} large={4} md={6} sm={12} xs={12} key={item.id}>
                        <CardPic 
                            // key={item.id}
                            image={require('../assets/images/Projects' + item.image)}
                            title={item.title}
                            description={item.description}
                            style={styles.cardStyle}
                            // languages={item.languageIcons}
                            localIcons={item.localIcons}
                            github={item.github}
                            demo={item.demo}
                        />
                    </Col>
                    ))}

                </Row>
            </Container>  
        </div>
    )
}

const styles= {

    screenContainer: {
        paddingTop: '4em',
        paddingBottom: '10em',
    },
    cardStyle: {
        boxShadow: '0 8px 6px -6px black',
        borderRadius: '55px 55px 55px 55px',
        height: '50vh',
        width: '50vh',
        maxHeight: '50vh',
        overflow: "hidden",
        cursor: "pointer"
    },


}

export default Portfolio;
