import React from 'react'
import { Row, Col, Image, Container } from 'react-bootstrap'
import {useSpring, animated} from 'react-spring'
import { Link } from 'react-scroll'


const Banner = () => {

    const fadeInTop = useSpring({
        config: {duration: 1000},
        from: {opacity: 0, marginTop: -80},
        to: {opacity: 1, marginTop: 0},
    })


    return (
        <div className=".img-fluid wallpaper" style={styles.bannerWrapper}>
            <Container>
                <Row>
                    <animated.div style={fadeInTop}>
                        <div style={styles.brand}>
                            <h1>Full Stack Web Developer</h1>
                            <h2>Bridging UX & UI to clients needs . </h2>
                        </div>
                    </animated.div> 
                </Row>
                <Row>
                    <div style={styles.buttonDiv}>
                        <Link to='about' smooth={true} duration={2000}>
                            <h3 style={{color: 'red'}}>HELLO</h3>
                        </Link>
                    </div>
                </Row>
        </Container>
    </div>
    )
}

const styles = {
    bannerWrapper: {
        backgroundColor: '#353836',
        height: '100vh',

    },
    brand: {
        fontStyle: 'italic',
        color: 'white'
    },
    buttonDiv: {
        color: 'white',
        backgroundColor: 'white',
        borderRadius: '0.5em',
        width: '8em',
        height: '4em',
    }
}
export default Banner;
