import React, { useState } from 'react';
import emailjs from 'emailjs-com';
import { Form, Row, Col, Button } from 'react-bootstrap'

const ContactForm = () => {

    const [validated, setValidated] = useState(false);
    const [emailText, setEmailText] = useState("")
    const [nameText, setNameText] = useState("")
    const [companyText, setCompanyText] = useState("")
    const [Reason, setReason] = useState("")
    const [messageText, setMessageText] = useState("")

    const emailHandler =(e)=>{
        setEmailText(e.target.value) 
    }
    const nameHandler =(e)=>{
        setNameText(e.target.value) 
    }
    const companyHandler =(e)=>{
        setCompanyText(e.target.value) 
    }
    const handleReason= (e) =>{
        setReason(e.target.value)   
    }
    const handleMessage= (e) =>{
      setMessageText(e.target.value)   
  }

  const formCheck= (e)=> {
    const form = e.currentTarget;
    if (form.checkValidity() === false) {
      e.preventDefault();
      e.stopPropagation();
    }
    setValidated(true);
    sendEmail(e);
  };


const sendEmail = (e) => {
if(validated === true){
  e.preventDefault();

 let templateParams = {
    name: nameText,
    email: emailText,
    company: companyText,
    reason: Reason, 
    message: messageText
  }

    emailjs.sendForm('stevensgmail', 'contact_form', templateParams, 'user_iE2OnCTdGFWesLXzFqm3O')
      .then((result) => {
          console.log(result.text);
          clearForm();
      }, 
      (error) => {
          console.log(error.text);

      });
    }
  }

  const clearForm = () => {
    setNameText("");
    setEmailText("");
    setMessageText("");
    setReason("");
    setCompanyText("");

  }


  return (

    <Form  noValidate validated={validated} onSubmit={formCheck}>
        <Row>
            <Col xs={12} sm={12} md={6} large={6} xl={6} >

                <Form.Group controlId="contactForm.Name" value={nameText} onChange={nameHandler} >
                    <Form.Label>Name</Form.Label>
                    <Form.Control required type="name" placeholder="John Smith" />  
                  <Form.Control.Feedback type="invalid">
                      Please provide your name.
                  </Form.Control.Feedback>
                </Form.Group>

                <Form.Group controlId="contactForm.Email" value={emailText} onChange={emailHandler} >
                    <Form.Label>Email</Form.Label>
                    <Form.Control required type="email" placeholder="address@service.com" />
                    <Form.Control.Feedback type="invalid">
                      Please provide your email.
                    </Form.Control.Feedback>
                </Form.Group>

                <Form.Group controlId="contactForm.Company" value={companyText} onChange={companyHandler} >
                    <Form.Label>Company</Form.Label>
                    <Form.Control required type="company" placeholder="Company Name" />
                    <Form.Control.Feedback type="invalid">
                      Please provide your company or affiliation.
                    </Form.Control.Feedback>
                </Form.Group>

                <Form.Group controlId="contactForm.Reason">
                  <Form.Label>Reason for Contact</Form.Label>
                    <Form.Control required as="select" value={Reason} onChange={handleReason}>
                      <option value="">What's this about?</option>
                      <option value="Business Oppurtunity">Business Opportunity</option>
                      <option value="Prosepcive Employer">Prospective Employer</option>
                      <option value="Introduction">Just Wanted to Introduce Myself</option>
                      <option value="Personal">Personal</option>
                      <option value="Other">Other</option>
                  </Form.Control>
                  <Form.Control.Feedback type="invalid">
                      Please provide a reason for contact.
                  </Form.Control.Feedback>
                </Form.Group>

            </Col>

            <Col xs={12} sm={12} md={6} large={6} xl={6} >
                <Form.Group controlId="contactForm.Message">
                  <Form.Label>Message</Form.Label>
                   <Form.Control required as="textarea" rows="10"  
                      value={messageText} onChange={handleMessage}
                      style={styles.messageBox}/>
                  <Form.Control.Feedback type="invalid">
                      Please provide a brief message.
                  </Form.Control.Feedback>
                </Form.Group>
            </Col>

        </Row>

        <Row>
          <Col xs={12} sm={12} md={12} large={12} xl={12}>
            <Button variant="outline-success" size="lg" type="submit" value="Send" block>
                Send 
            </Button>
          </Col>
        </Row>

  </Form>



  );
}


const styles = {
  messageBox: {
    height: '40vh',
    resize: 'none'
  }
}

export default ContactForm;
