import React from 'react';
import { Image, Row, Col, Card } from 'react-bootstrap'



const ProjectsCard = props =>{

    return(
        <div>

        <Row style={styles.projectDiv} key={props.id}>
            <Col md={4}>
                <div style={styles.imgContainer}>
                    <Image src={require('../../assets/images/Projects' + props.image)} 
                        style={styles.projImg}/>
                </div>
            </Col>
            <Col>
              <Card.Body style={styles.cardBody}>
                <Card.Title className="text-center flex-start">
                    {props.title}
                </Card.Title>
                <Card.Text className="text-center">
                    {props.description}
                </Card.Text>
                <div className="d-flex  align-content-end">
                    {props.icons ? (props.icons.map((item, index )=>
                        <Image src={require('../../assets/images/Icons' + item)} style={styles.logo} key={index}/>
                        )):(null)}
                </div>
              </Card.Body>
            </Col>
        </Row>




            <div class="row">
                <div class="col-md-4">
                    <img src="..." class="card-img" alt="..." />
                </div>      
                <div class="col-md-8">
                    <div class="card-body">
                        <h5 class="card-title">Card title</h5>
                        <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                        <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
                    </div>
                 </div>
            </div>



</div>

    )

}

const styles={
    projectDiv:{
        marginTop: '2em',
        marginBottom: '2em',

    },
    imgContainer:{
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        maxHeight: '50vh',
        maxWidth: '50vh',
        overflow: "hidden",
        boxShadow: '0 8px 6px -6px black',
    },
    projImg:{
        maxWidth: '100%',
        maxHeight: '100%',

    },
   cardBody: {
       display: 'flex',
       flexDirection: 'column', 
       alignItems: 'center',
   },
   logo: {
    height: '3em',
    width: '3em',
    color: 'white',
    margin: '0.4em'   
},
}

export default ProjectsCard;
