import React from 'react';
import { Container, Row, Col, Image } from 'react-bootstrap'
import {useSpring, useChain, animated} from 'react-spring'
import RoadPic from '../../assets/images/Backgrounds/JakeBlucker.jpg'
import AboutCard from '../Design/AboutCard'
import Headshot from '../Design/Headshot'
import '../../assets/css/home.css'

const AboutMe = () => {

    const fadeTop = useSpring({
        config: {duration: 800},
        from: {opacity: 0, marginTop: -80},
        to: {opacity: 1, marginTop: 0},
    })

    const fadeInTop = useSpring({
        config: {duration: 1000},
        from: {opacity: 0, marginTop: -80},
        to: {opacity: 1, marginTop: 0},
    })

    return(

            <div className=".img-fluid wallpaper" id="about" style={{backgroundImage: `url(${RoadPic})`}}>
                <Container style={styles.screenContainer}>
                    <Row>
                        <animated.div style={fadeInTop}>
                            <div style={styles.brand}>
                                <h1>Full Stack Web Developer</h1>
                                <h2>Bridging UX & UI to clients needs . </h2>
                            </div>
                        </animated.div> 
                    </Row>


                    <Row>
                        <Col>
                            <animated.div style={fadeTop}>
                                <AboutCard />
                            </animated.div>
                        </Col>

                        <Col xs={12} sm={12} md={6} lg={6} xl={6}>
                            <animated.div style={fadeTop}>
                                <Headshot imageSource={require('../../assets/images/Personal/Balcony.JPG')}/>
                            </animated.div>
                        </Col>
                    </Row>

                    <Row className="justify-content-center m-5"> 
                        <Image src={require('../../assets/images/Icons/NodeLogo.svg')} style={styles.logo} />
                        <Image src={require('../../assets/images/Icons/react.png')} style={styles.logo} />
                        <Image src={require('../../assets/images/Icons/javascript.png')} style={styles.logo} />
                    </Row>

                </Container>
            </div>



    )
}

const styles ={

    brand: {
        fontStyle: 'italic',
        color: 'white'
    },
    screenContainer: {
        paddingTop: '4em',
        paddingBottom: '10em',
    },
    logo: {
        height: '4em',
        size: '4em',
        margin: '1em'
    }


}

export default AboutMe;
