import React from 'react';
import {Card} from 'react-bootstrap'

const CardPic = props => {

    return(

        <div>

        <Card className="bg-dark text-white text-center justify-content-center align-items-center" style={props.style} key={props.id}>
            <Card.Img src={props.image} alt={props.title} style={styles.imageContainer}/>

         </Card>

        <div style={props.style}>
         <Card className="bg-dark text-white text-center justify-content-center align-items-center" style={styles.cardBack} key={props.id}>
                <Card.Body>
                    <Card.Title>{props.title}</Card.Title>
                    <Card.Subtitle className="mb-2 text-muted">LANGUAGES HERE</Card.Subtitle>
                    <Card.Text>
                        {props.decription}
                    </Card.Text>
                    <Card.Link href={props.github}>Code</Card.Link>
                    <Card.Link href={props.demo}>Demo</Card.Link>
            </Card.Body>
         </Card>
        </div>




         </div>
    )
}
const styles={
    imageContainer: {
        maxWidth: '100%',
        maxHeight: '100%',
        boxShadow: '0 8px 6px -6px black',
        borderRadius: '55px 55px 55px 55px',
    },
    cardBack: {
        maxWidth: '100%',
        maxHeight: '100%',
        boxShadow: '0 8px 6px -6px black',
        borderRadius: '55px 55px 55px 55px',
    },

}

export default CardPic;
