import React from 'react';
import {Card} from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Fire from '../../assets/images/CustomIcons/Firebase-RD.png'

const CardPic = props => {

    return(

        <div >

        <Card className="bg-dark text-white text-center justify-content-center align-items-center" style={props.style} key={props.id}>

            <Card.Img src={props.image} alt={props.title} style={styles.imageContainer}/>

         </Card>


         <Card className="bg-dark text-white text-center align-items-center justify-content-center" 
            style={props.style}  
            key={props.id}>
                <Card.Body className="d-flex flex-column align-items-center justify-content-around">
                    <div>
                        <Card.Title>
                            {props.title}
                        </Card.Title>
                        <Card.Subtitle >

                        {props.languages ? ((props.languages.map(item =>
                            <FontAwesomeIcon style={styles.logo} icon={item}/>
                            ))): (null)}

                            <img  src={props.localIcons} style={styles.logo}/>


                        </Card.Subtitle>
                    </div>
                    <Card.Text>
                        {props.description}
                    </Card.Text>
                    <div>
                        <Card.Link href={props.github}>Code</Card.Link>
                        <Card.Link href={props.demo}>Demo</Card.Link>
                    </div>
                 </Card.Body>
         </Card>





         </div>
    )
}


// <Card.ImgOverlay className="text-center d-flex flex-column justify-content-center">
// <Card.Title>{props.title}</Card.Title>
// <Card.Text>
// {props.description}
// </Card.Text>
// </Card.ImgOverlay>

const styles={
    imageContainer: {
        width: '100%',
        height: '100%',
        boxShadow: '0 8px 6px -6px black',
        borderRadius: '55px 55px 55px 55px',

    },
    logo: {
        height: '1em',
        width: '1em',
        color: 'white',
        margin: '0.3em'
    }

}

export default CardPic;
