import React from 'react';
import { Row, Col, Card } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'


const ProjectsCard = props =>{

    return(

        <Row style={styles.projectDiv} key={props.id}>
            <Col md={6}>
                <div style={styles.imgContainer} onClick={()=>window.open(props.demo)}>
                    <Card.Img src={require('../../assets/images/Projects' + props.image)} 
                        style={styles.projImg}/>
                </div>
            </Col>
            <Col md={6}>
              <Card.Body style={styles.cardBody}>
                <Card.Title className="text-center flex-start">
                    {props.title}
                </Card.Title>
                <Card.Subtitle>
                    <FontAwesomeIcon onClick={()=>window.open(props.github)} style={styles.FAIcon} icon="code" />
                    <FontAwesomeIcon onClick={()=>window.open(props.demo)} style={styles.FAIcon} icon="eye"/>
                </Card.Subtitle>
                <Card.Text className="text-center mt-5">
                    {props.description}
                </Card.Text>
                <div className="d-flex align-content-end">
                    {props.icons ? (props.icons.map((item, index )=>
                        <Card.Img src={require('../../assets/images/Icons' + item)} style={styles.logo} key={index}/>
                        )):(null)}
                </div>
              </Card.Body>
            </Col>
        </Row>

    )

}

const styles={
    projectDiv:{
        marginTop: '2em',
        marginBottom: '2em',
    },
    imgContainer:{
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        overflow: "hidden",
        boxShadow: '0 8px 6px -6px black',
        width: '100%',
        height: '300px',
    },
    projImg:{
        width: '100%',
        height: '100%',
        display: 'block',
        marginLeft: 'auto',
        marginRight: 'auto',
    },
   cardBody: {
       display: 'flex',
       flexDirection: 'column', 
       alignItems: 'center',
       justifyContent: 'center'
   },
   logo: {
    height: '3em',
    width: '3em',
    color: 'white',
    margin: '0.4em'   
},
FAIcon: {
    height: '1.3em',
    width: '1.3em',
    color: 'black',
    margin: '0.4em'   
},
}

export default ProjectsCard;
