import React from 'react';
import {Card, Image} from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'


const CardPic = props => {

    return(

        <div style={styles.wrapper}>

            <Card className="bg-dark text-white text-center justify-content-center align-items-center" style={props.style} >

                <Card.Img src={props.image} alt={props.title} style={styles.imageContainer}/>

            </Card>


         <Card className="bg-dark text-white text-center align-items-center justify-content-center" style={props.style} >
                <Card.Body className="d-flex flex-column align-items-center justify-content-around">
                    <div>
                        <Card.Title>
                            {props.title}
                        </Card.Title>
                        <Card.Subtitle style={styles.subtitle}>
                        {props.localIcons ? 
                                (props.localIcons.map(logo =>
                                    <Image  src={require('../../assets/images/Icons'+ logo)} style={styles.logo}/>)):(null)}
                        </Card.Subtitle>
                    </div>
                    <Card.Text>
                        {props.description}
                    </Card.Text>
                    <div>
                        <Card.Link href={props.github}>Code</Card.Link>
                        <Card.Link href={props.demo}>Demo</Card.Link>
                    </div>
                 </Card.Body>
         </Card>





         </div>
    )
}




const styles={
    wrapper : {
        position: 'relative'
    },
    imageContainer: {
        width: '100%',
        height: '100%',
        boxShadow: '0 8px 6px -6px black',
        borderRadius: '55px 55px 55px 55px',

    },
    logo: {
        height: '2em',
        width: '2em',
        color: 'white',
        margin: '0.4em'

    },
    subtitle: {
        marginTop: '1em'
    }


}

export default CardPic;
