import React from 'react';
import CardPic from '../components/Design/CardPic'
import { Container, Row, Col } from 'react-bootstrap'
import Squares from '../assets/images/Backgrounds/GabrielBassinoSquares.jpg'
import '../assets/css/home.css'
import ProjectsData from '../assets/ProjectsData.json'


const Portfolio = () => {


    return(
        <div className="wallpaper" style={{backgroundImage: `url(${Squares})`}}>
            <Container style={styles.screenContainer}>


                <Row>
                {ProjectsData.map(item=>(
                    <Col  xl={4} large={4} md={6} sm={12} xs={12} key={item.id}>
                        <CardPic 
                            key={item.id}
                            image={require('../assets/images/Projects' + item.image)}
                            title={item.title}
                            description={item.description}
                            style={styles.cardStyle}
                            // languages={item.languageIcons}
                            localIcons={item.localIcons}
                            github={item.github}
                            demo={item.demo}
                        />
                    </Col>
                    ))}
                </Row>
            </Container>  
        </div>
    )
}



const styles= {

    screenContainer: {
        paddingTop: '4em',
        paddingBottom: '10em',
    },
    cardStyle: {
        boxShadow: '0 8px 6px -6px black',
        borderRadius: '55px 55px 55px 55px',
        height: '50vh',
        width: '50vh',
        maxHeight: '50vh',
        overflow: "hidden",
        cursor: "pointer"
    },


}

export default Portfolio;
