import React from 'react';
import {Card} from 'react-bootstrap'

const CardPic = props => {

    return(

        <Card className="bg-dark text-white text-center justify-content-center align-items-center" style={styles.cardContainer} key={props.id}>
            <Card.Img src={props.image} alt={props.title} style={styles.imageContainer}/>
            <Card.ImgOverlay className="text-center d-flex flex-column justify-content-center">
                <Card.Title>{props.title}</Card.Title>
                <Card.Text>
                {props.description}
                </Card.Text>
            </Card.ImgOverlay>

            </Card>
    )
}
const styles={
    imageContainer: {
        maxWidth: '100%',
        maxHeight: '100%'
    },
    cardContainer:  {
        boxShadow: '0 8px 6px -6px black',
        borderRadius: '10em 10em 10em 10em',
    }

}

export default CardPic;
